import { DISPLAY_ALERT } from "./action";



const reducer=(state,action)=>{

if(action.type===DISPLAY_ALERT)
{
return {...state,showAlert:true,}


}





throw new Error(`No such action : ${action.type}`)


}

export default reducer;
