import { useState,useEffect } from "react"
import { Logo,FormRow,Alert} from "../components"
import Wrapper from "../assets/wrappers/RegisterPage"



const initialState={
name:'',
email:'',
password:'',
isMember:true,
showAlert:false
}
const Register = () => {
const [values,setValues]=useState(initialState)

//global state and useNavigate

const toggleMember=()=>{

setValues({...values,isMember:!values.isMember})


}



const handleChange=(e)=>{

  console.log(e.target);
}


const onSubmit=(e)=>{
  e.preventDefault();
  console.log(e.target)

}




  return (
<Wrapper className='full-page'>
  <form className="form" onSubmit={onSubmit}>

<Logo/>
<h3>{values.isMember?'Login':'Register'}</h3>
{values.showAlert || <Alert/>}
 {/*name input */}
 {!values.isMember && 
<FormRow type="text" name="name" values={values.name} handleChange={handleChange}/>
 }

 {/*email input */}
 <FormRow type="email" name="email" values={values.email} handleChange={handleChange}/>
 {/*name input */}
 <FormRow type="password" name="password" values={values.password} handleChange={handleChange}/>

<button type="submit" className="btn btn-block">
  submit
</button>
<p>
  {values.isMember}
  <button type="button" onClick={toggleMember}
  className="member-btn">
Register
  </button>
</p>
  </form>
</Wrapper>
  )
}

export default Register
